mapper.select:
SELECT PROJECT_SEQ
FROM PROJECT_INFO
WHERE PROJECT_NAME = #{pname}

SELECT COUNT(*)
FROM PROJECT_INFO
WHERE PROJECT_NAME = #{pname}

mapper.update:
UPDATE project_info
SET FILE_SAVE_PATH = #{file_save_path}
WHERE PROJECT_SEQ = #{project_seq}

UPDATE project_info
SET PROJECT_CONTENTS = #{project_contents}
WHERE PROJECT_SEQ = #{project_seq}
